const file = items[0].binary.File;


file.fileExtension = 'zip';
file.mimeType = 'application/zip';


file.fileName = file.fileName.replace(/\.docx$/i, '.zip');

return items;

const xml = $json.File; 


const pRegex = /<w:p\b[^>]*>[\s\S]*?<\/w:p>/g;
const tRegex = /<w:t\b[^>]*>([\s\S]*?)<\/w:t>/g;


const paragraphs = []; 
const runs = []; 
let globalRunIndex = 0;

let pMatch;
while ((pMatch = pRegex.exec(xml)) !== null) {
  const pXml = pMatch[0];

  const textParts = [];
  const runIndices = [];

  let tMatch;
  while ((tMatch = tRegex.exec(pXml)) !== null) {
    const innerText = tMatch[1] ?? '';


    textParts.push(innerText);
    runIndices.push(globalRunIndex);

    runs.push({
      index: globalRunIndex,
      text: innerText,
      paragraphId: paragraphs.length,
    });

    globalRunIndex++;
  }

  const paragraphText = textParts.join('');


  if (paragraphText.trim().length === 0) {
    continue;
  }

  paragraphs.push({
    id: paragraphs.length,
    text: paragraphText,
    runIndices,
  });
}


return {
  json: {
    ...$json,
    originalXml: xml,
    paragraphs,
    runs,
  },
};

const llmItems = $input.all();

const segmentation = $('Segmentation').first().json;
const originalXml = segmentation.originalXml;
const runs = segmentation.runs;


const translations = llmItems.map(item => ({
  id: item.json.id,                 
  runIndices: item.json.runIndices, 
  translatedText: item.json.text,  
}));


return [
  {
    json: {
      originalXml,
      runs,
      translations,
    },
  },
];

const originalXml = $json.originalXml;
const runs = $json.runs;
const translations = $json.translations;


for (const t of translations) {
  const { runIndices, translatedText } = t;

  if (!translatedText || !runIndices || runIndices.length === 0) continue;

  const paraRuns = runIndices.map(i => runs[i]);
  const totalLen = paraRuns.reduce((sum, r) => sum + (r.text || '').length, 0) || 1;

  let offset = 0;
  for (let i = 0; i < runIndices.length; i++) {
    const runIndex = runIndices[i];
    const run = runs[runIndex];

    const origLen = (run.text || '').length;
    const isLast = i === runIndices.length - 1;

    let share;
    if (isLast) {
      share = translatedText.length - offset;
    } else {
      share = Math.round(translatedText.length * origLen / totalLen);
    }
    if (share < 0) share = 0;

    const part = translatedText.slice(offset, offset + share);
    offset += share;

    run.translatedText = part;
  }
}


for (const run of runs) {
  if (typeof run.translatedText !== 'string') {
    run.translatedText = run.text || '';
  }
}

function escapeXml(str) {
  return String(str)
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;');
}


let runCounter = 0;
const tRegex = /<w:t\b([^>]*)>([\s\S]*?)<\/w:t>/g;

const newXml = originalXml.replace(tRegex, (match, attrs, inner) => {
  const run = runs[runCounter++];

  if (!run) {
    return match;
  }

  const replacementText = escapeXml(run.translatedText);
  return `<w:t${attrs}>${replacementText}</w:t>`;
});

return {
  json: {
    ...$json,
    newDocumentXml: newXml,
  },
};

for (const item of items) {
  if (!item.binary) continue;

  for (const key of Object.keys(item.binary)) {
    const file = item.binary[key];
    if (!file || typeof file !== 'object') continue;


    if (!file.fileExtension && (!file.data || file.data.length === 0)) {
      delete item.binary[key];
      continue;
    }

    if (file.directory) {
      file.fileName = `${file.directory}/${file.fileName}`;
    }
  }
}

return items;

const item = $input.item;
const binaries = item.binary || {};
const newXml = item.json.newDocumentXml;

for (const [key, bin] of Object.entries(binaries)) {
  if (!bin || typeof bin !== 'object') continue;

  const name = bin.fileName || '';
  const dir = bin.directory || '';


  if (name === 'document.xml' && dir === 'word') {
    bin.data = Buffer.from(String(newXml), 'utf8').toString('base64');
    bin.mimeType = 'application/xml';
  }
}

item.binary = binaries;
return item;
